import { useEffect, useState } from "react"
import { Input } from "../form/Input"
import { Select } from "../form/Select"
import { SubmitButton } from "../form/SubmitButton"

export const ProjectForm = ({ btnText }) => {
    // o useState foi usado para pegar os dados da api, e setar em categories
    const [categories, setCategories] = useState([])

    // o useEffect, corrigi o problema de várias requests sendo enviadas, e renderiza uma única vez por load
    useEffect(() => {
        fetch('http://localhost:5000/categories', {
            method: 'get',
            headers: { 'Content-Type': 'application/json' }
        })
            .then((res) => res.json())
            .then((data) => setCategories(data))
            .catch((e) => console.log(e))
    }, [])

    return (
        <form>
            <Input 
                type='text'
                text='Nome do projeto'
                name='name'
                placeholder='Insira o nome do projeto'
            />
            <Input 
                type='number' 
                text='Orçamento do projeto'
                name='budget'
                placeholder='Insira o orçamento total' 
            />
            <Select 
                name='category_id'
                text='Selecione a categoria'
                options={categories}
            />
            <SubmitButton  
                text={btnText}
            />
        </form>
    )
}
