import styles from './Project.module.css'
import { useParams } from 'react-router-dom'
import { useEffect, useState } from 'react'
import { Loading } from '../../components/layout/loaders/Loading'
import { Container } from '../../components/layout/case/Container'
import { ProjectForm } from '../../components/project/ProjectForm'
import { Message } from '../../components/layout/messages/Message'

export const Project = () => {
  const { id } = useParams()
  const [project, setProject] = useState([])
  const [showProjectForm, setShowProjectForm] = useState(false)
  const [showServiceForm, setShowServiceForm] = useState(false)
  const [message, setMessage] = useState()
  const [typeMessage, setTypeMessage] = useState()

  useEffect(() => {
    fetch(`http://localhost:5000/projects/${id}`, {
      method: 'GET',
      headers: { 'Content-type': 'application/json' }
    })
      .then(res => res.json())
      .then(data => setProject(data))
      .catch()
  }, [id])

  const editPost = (project) => {
    // budget validation
    if (project.budget < project.cost) {
      setMessage('O orçamento não pode ser menor que o custo do projeto!')
      setTypeMessage('error')
      return false
    }

    fetch(`http://localhost:5000/projects/${project.id}`, {
      method: 'PATCH',
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify(project)
    })
      .then(res => res.json())
      .then(data => {
        setProject(data)
        setShowProjectForm(false)
        setMessage('Projeto atualizado!')
        setTypeMessage('sucess')
      })
      .catch(e => console.log(e))
  }

  const toggleProjectForm = () => {
    setShowProjectForm(!showProjectForm)
  }

  const toggleServiceForm = () => {
    setShowServiceForm(!showServiceForm)
  }

  return <> {
    project.name ? (
      <div className={styles.project_details}>
        <Container customClass='column'>
          {message && <Message type={typeMessage} msg={message} />}
          <div className={styles.details_container}>
            <h1>Projeto: {project.name}</h1>
            <button className={styles.btn} onClick={toggleProjectForm}>{
              !showProjectForm ? 'Editar projeto' : 'Fechar'
            }</button>
            {!showProjectForm ? (
              <div className={styles.project_info}>
                <p>
                  <span>Categoria: </span> {project.category.name}
                </p>
                <p>
                  <span>Total de Orçamento: </span> R${project.budget}
                </p>
                <p>
                  <span>Total Utilizado: </span> R${project.cost}
                </p>
              </div>
            ) : (
              <div className={styles.project_info}>
                <ProjectForm
                  handleSubmit={editPost}
                  btnText='Concluir edição'
                  projectData={project}
                />
              </div>
            )}
          </div>
          <div className={styles.service_form_container}>
            <h2>Adicione um serviço: </h2>
            <button className={styles.btn} onClick={toggleServiceForm}>
              {!showServiceForm ? 'Adicionar serviço' : 'Fechar'}
            </button>
            <div className={styles.project_info}>
              {showServiceForm && (
                <div>Formulário do serviço</div>
              )}
            </div>
          </div>
          <h2>Serviços</h2>
          <Container customClass='start'>
            <p>Itens de serviços</p>
          </Container>
        </Container>
      </div>) : (
      <Loading />
    )}
  </>
}
